import { observer } from "mobx-react-lite";
import React, { useContext } from "react";
import { Card, Col } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import { useNavigate } from "react-router-dom";
import { Context } from "..";
import star from "../assets/star.png";
import { DEVICE_ROUTE } from "../utils/consts";

const DeviceItem = observer(({ currentDevice }) => {
	const device = useContext(Context);
	let brand;
	for (const el in device.brands) {
		if (el.id == device.brandId) {
			brand = el.name;
		}
	}
	const navigate = useNavigate();
	return (
		<Col
			md={3}
			className='mt-3'
			onClick={() => navigate(DEVICE_ROUTE + "/" + currentDevice.id)}
		>
			<Card style={{ width: 150, cursor: "pointer" }} border={"light"}>
				<Image
					width={150}
					height={150}
					currentDevice
					style={{ objectFit: "contain" }}
				/>
				<div className='mt-2 d-flex justify-content-between'>
					<div className='text-black-50'>{brand}</div>
					<div className='d-flex align-items-center'>
						<div>{currentDevice.rating}</div>
						<Image src={star} width={13} height={13} />
					</div>
				</div>
				<div> {currentDevice.name}</div>
			</Card>
		</Col>
	);
});

export default DeviceItem;
