import React, { useContext, useState, useEffect } from "react";
import { Button, Row, Col, Dropdown, Form, Modal } from "react-bootstrap";
import { Context } from "../..";
import { fetchBrands, fetchTypes } from "../../http/deviceAPI";
import { observer } from "mobx-react-lite";
const CreateDevice = observer(({ show, onHide }) => {
	const { device } = useContext(Context);
	const [info, setInfo] = useState([]);
	const [name, setName] = useState("");
	const [price, setPrice] = useState(0);
	const [file, setFile] = useState(null);
	const [brand, setBrand] = useState(null);
	const [type, setType] = useState(null);
	const addInfo = () => {
		setInfo([...info, { title: "", description: "", number: Date.now() }]);
	};
	const removeInfo = (number) => {
		setInfo(info.filter((i) => i.number !== number));
	};
	const changeInfo = (key, value, number) => {
		setInfo(
			info.map((i) => (i.number === number ? { ...i, [key]: value } : i))
		);
	};

	const selectFile = (e) => {
		setFile(e.target.files[0]);
	};
	const addDevice = () => {
		const formData = new FormData();
		FormData.append("name", name);
		FormData.append("price", `${price}`);
		FormData.append("img", file);
		FormData.append("brandId", device.selectedType.id);
		FormData.append("typeId", device.selectedBrand.id);
	};
	useEffect(() => {
		fetchTypes().then((data) => device.setTypes(data));
		fetchBrands().then((data) => device.setBrands(data));
	}, []);
	return (
		<Modal size='lg' centered show={show} onHide={onHide}>
			<Modal.Header closeButton>
				<Modal.Title id='contained-modal-title-vcenter'>
					Add New Device
				</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				<Form>
					<Dropdown className='mt-2 mb-2'>
						<Dropdown.Toggle>
							{device.selectedType.name || "Choose Type"}
						</Dropdown.Toggle>
						<Dropdown.Menu>
							{device.types.map((type) => (
								<Dropdown.Item
									key={type.id}
									onClick={() => device.setSelectedType(type)}
								>
									{type.name}
								</Dropdown.Item>
							))}
						</Dropdown.Menu>
					</Dropdown>
					<Dropdown className='mt-2 mb-2'>
						<Dropdown.Toggle>
							{device.selectedBrand.name || "Choose Brand"}
						</Dropdown.Toggle>
						<Dropdown.Menu>
							{device.brands.map((brand) => (
								<Dropdown.Item
									key={brand.id}
									onClick={() =>
										device.setSelectedBrand(brand)
									}
								>
									{brand.name}
								</Dropdown.Item>
							))}
						</Dropdown.Menu>
					</Dropdown>
					<Form.Control
						value={name}
						onChange={(e) => setName(e.target.value)}
						className='mt-3'
						placeholder='Add device name'
					/>
					<Form.Control
						value={price}
						onChange={(e) => setPrice(e.target.value)}
						className='mt-3'
						placeholder='Add device price'
						type='number'
					/>
					<Form.Control
						className='mt-3'
						type='file'
						onChange={selectFile}
					/>
					<hr />
					<Button variant='outline-dark' onClick={addInfo}>
						Add new property
					</Button>
					{info.map((i) => (
						<Row className='mt-3' key={i.number}>
							<Col md={4}>
								<Form.Control
									placeholder='Add new property name'
									value={i.title}
									onChange={(e) =>
										changeInfo(
											"title",
											e.target.value,
											i.number
										)
									}
								/>
							</Col>
							<Col md={4}>
								<Form.Control
									placeholder='Add property description'
									value={i.description}
									onChange={(e) =>
										changeInfo(
											"description",
											e.target.value,
											i.number
										)
									}
								/>
							</Col>
							<Col md={4}>
								<Button
									variant='outline-danger'
									onClick={() => removeInfo(i.number)}
								>
									Delete
								</Button>
							</Col>
						</Row>
					))}
				</Form>
			</Modal.Body>
			<Modal.Footer>
				<Button variant='outline-danger' onClick={onHide}>
					Close
				</Button>
				<Button variant='outline-success' onClick={addDevice}>
					Add
				</Button>
			</Modal.Footer>
		</Modal>
	);
});

export default CreateDevice;
