import React from "react";
import { Button, Card, Col, Container, Row } from "react-bootstrap";
import bigStar from "../assets/bigStar.png";
import Image from "react-bootstrap/Image";
const DevicePage = () => {
	const device = {
		id: 1,
		name: "Iphone 12 pro",
		price: 25000,
		rating: 5,
		img: "https://object.pscloud.io/cms/cms/Photo/img_0_77_2624_0_1.jpg",
	};
	const descirption = [
		{ id: 1, title: "RAM", descirption: "5 GB" },
		{ id: 2, title: "Camera", descirption: "12 MP" },
		{ id: 3, title: "Processor", descirption: "M1" },
		{ id: 4, title: "Core", descirption: "5" },
		{ id: 5, title: "Battery", descirption: "4000 mA/h" },
	];
	return (
		<Container>
			<Row>
				<Col md={4}>
					<Image width={300} height={300} src={device.img} />
				</Col>
				<Col md={4}>
					<Row className='d-flex flex-column align-items-center'>
						<h2>{device.name}</h2>
						<div
							className='d-flex justify-content-center align-items-center'
							style={{
								background: `url(${bigStar}) no-repeat center center`,
								width: "240px",
								height: "240px",
								backgroundSize: "cover",
								fontSize: 64,
							}}
						>
							{device.rating}
						</div>
					</Row>
				</Col>
				<Col md={4}>
					<Card
						className='d-flex flex-column align-items-center justify-content-around'
						style={{
							width: 300,
							height: 300,
							fontsize: 42,
							border: "5px solid lightgray",
						}}
					>
						<h3>{device.price}</h3>
						<Button variant={"outline-dark"}>Add to Cart</Button>
					</Card>
				</Col>
			</Row>
		</Container>
	);
};

export default DevicePage;
